import React,{ useState } from 'react'
import {Link} from 'react-router-dom'
import { useNavigate } from 'react-router-dom'

import Validation from './signUpValidation'
import axios from 'axios'


function SelectLanguage() {
    const [values,setValues] = useState({
        lanuageOption:''

  })
  
  const navigate = useNavigate();
  const [errors,setErrors] = useState({})
  const handleInput = (event)=>{
      setValues(prev => ({...prev,[event.target.name]:[event.target.value]}))
  }
    const handleSubmit =(event)=>{
        event.preventDefault();
        // setErrors(Validation(values));
        if(errors.lanuageOption===""){
          axios.get('http://localhost:5000/selectLanguage', values)
          .then(res => {
            console.log('Check')
            navigate('/');
            alert("Thanks");     
          })
          .catch(err => console.log(err));
        }
    }
    // const handleSubmit= async(e)=>{
    //    e.preventDefault();
    //    const allInputvalue= {language:formValue.language}; 

    //   let res= await fetch("http://localhost:5000/selectLanguage",{
    //     method:"POST",
    //     headers:{'content-type':'application/json'},
    //     body:JSON.stringify(allInputvalue)
    //   });
    //   let resjson= await res.json();
    //   if(res.status===200)
    //   {
    //     setMessage(resjson.success);
    //     setTimeout(()=>{
    //         navigate('/');
    //     }, 2000);

    //   } else{
    //     setMessage("Some Error Occured");
    //   }

    //    //console.log(allInputvalue);
    // }

    return(
        <React.Fragment>
            <div className="container">
                <div className="row">
                    <div className="col-md-12">
                    <h5 className="mt-2">Here we gooo!!!!</h5>
                    <p className="text-success"> { message } </p>
                    <form onSubmit={ handleSubmit}>
                        <div className="row">
                            <div className="col-md-6">
                                <div className="mb-3">
                                    <label className="form-lable">Select your Language</label>
                                    <select className="form-control" name="language" value={formValue.language} onChange={ handleInput}>
                                        <option value="">--Please Select--</option>
                                        <option value="Hindi">Hindi</option>
                                        <option value="Bengali">Bengali</option>
                                        <option value="Germany">Germany</option>
                                        <option value="French">French</option>
                                        <option value="Telegu">Urdu</option>
                                        <option value="Urdu">Telegu</option>
                                    </select>
                                    {/* <label className="form-lable">Select your Level</label>
                                    <select className="form-control" name="language" value={formValue.language} onChange={ handleInput}>
                                        <option value="">--Please Select--</option>
                                        <option value="Beginner">Beginner</option>
                                        <option value="Intermediate">Intermediate</option>
                                        <option value="Proficient">Proficient</option>
                                    </select> */}
                                </div>
                            </div>

                            <div className="col-md-12">
                                <div className="mb-3">
                                    <label className="form-lable"></label>
                                    <button type="submit" className="btn btn-success btn-lg">Submit</button>
                                     </div>
                            </div>
                        </div>
                    </form>
                    </div>
                </div>
            </div>
            
        </React.Fragment>
    );
}

export default SelectLanguage;
