import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom'
import axios from 'axios';

function CourseContent() {

    const [contentDetails, setContentDetails] = useState([]);
    useEffect(() => {
        fetchData();
    }, [])
   // Fetch course details table from backend API
    const fetchData = async () => {
        try {
            const result = await axios("http://localhost:5000/selectLanguage");
            //console.log(result.data);
            setContentDetails(result.data)
        } catch (err) {
            console.log("somthing Wrong");
        }
    }
  
  
    const handleChange = (event) => {
      setSelectedCourse(event.target.value);
    };
    
    const navigate = useNavigate();


    return (
        <div className='d-flex justify-content-center align-items-center bg-primary vh-100'>
        <div className='bg-white p-3 rounded w-25'>
        <h2>details you course</h2>
        <form>
         <div className="container">
          <h3>Course Details</h3>
           <table className="table table-bordered">
             <thead>
                <tr>
                    <th>S No.</th>
                    <th>Study material</th>
                </tr>
             </thead>
            <tbody>
                {
                contentDetails.map((Course, i) => {
                    return (
                        <tr key={i}>
                            <td>{i + 1}</td>
                            <td>{Course.content_url} </td>
                            
                        </tr>
                        )
                    })
                }
  
            </tbody>
        </table>
        </div>
        </form>
        </div>


        
        </div>
        
    );
  }
  


export default CourseContent
