import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom'
import axios from 'axios';
import {Link} from 'react-router-dom'

function CourseContent() {

    const [contentDetails, setContentDetails] = useState([]);
    // useEffect(() => {
    //     fetchData();
    // }, [])
  //  // Fetch content details table from backend API
  //   const fetchData = async () => {
  //       try {
  //           const result = await axios("http://localhost:5000/api/LearningDetails");
  //           //console.log(result.data);
  //           setContentDetails(result.data)
  //           console.log("somthing Wrong");
  //       } catch (err) {
  //       }
  //   }

  useEffect(() => {
    axios.get('http://localhost:5000/api/LearningDetails')
      .then(response => {
        setContentDetails(response.data);
      })
      .catch(error => {
        console.error('Error fetching course IDs:', error);
      });
  }, []);
  
    return (
        <div className='d-flex justify-content-center align-items-center bg-primary vh-100'>
        <div className='bg-white p-3 rounded w-25'>
        <form>
         <div className="container">
          <h3>Course Details</h3>
           <table className="table table-bordered">
             <thead>
                <tr>
                    <th>S No.</th>
                    <th>Study material</th>
                </tr>
             </thead>
            <tbody>
                {
                contentDetails.map((Course, i) => {
                    return (
                        <tr key={i}>
                            <td>{i + 1}</td>
                            <Link to='Course.content_url'><td>{Course.content_url} </td></Link>                       
                        </tr>
                        )
                    })
                }
  
            </tbody>
        </table>
        </div>
        </form>
        </div>      
        </div>
        
    );
  }
  


export default CourseContent
