import React, { useEffect, useState } from 'react';
import axios from 'axios';

function SelectLanguage() {
    const [courseIds, setCourseIds] = useState([]);
    const [selectedCourse, setSelectedCourse] = useState('');

    const [courseDetails, setCourseDetails] = useState([]);
    useEffect(() => {
        fetchData();
    }, [])
  
    const fetchData = async () => {
        try {
            const result = await axios("http://localhost:5000/selectLanguage");
            //console.log(result.data);
            setCourseDetails(result.data)
        } catch (err) {
            console.log("somthing Wrong");
        }
    }
  
    // Fetch course IDs from backend API
    useEffect(() => {
      axios.get('http://localhost:5000/selectLanguage')
        .then(response => {
          setCourseIds(response.data);
        })
        .catch(error => {
          console.error('Error fetching course IDs:', error);
        });
    }, []);
  
    const handleChange = (event) => {
      setSelectedCourse(event.target.value);
    };
  
    return (
        <div className='d-flex justify-content-center align-items-center bg-primary vh-100'>
        <div className='bg-white p-3 rounded w-25'>
        <h2>details you course</h2>
        <form action>
         <div className="container">
          <h3>Course Details</h3>
           <table className="table table-bordered">
             <thead>
                <tr>
                    <th>S No.</th>
                    <th>Course ID</th>
                    <th>Language</th>
                    <th>Level</th>
                </tr>
             </thead>
            <tbody>
                {
                courseDetails.map((Course, i) => {
                    return (
                        <tr key={i}>
                            <td>{i + 1}</td>
                            <td>{Course.course_id} </td>
                            <td>{Course.language} </td>
                            <td>{Course.level} </td>
                        </tr>
                        )
                    })
                }
  
            </tbody>
        </table>
        </div>

           </form>
        </div>
        <div className='bg-white p-3 rounded w-25'>
          <h2>details you course</h2>
           <form action>
            <div className='mb-3'>
                <select value={selectedCourse} onChange={handleChange}>
                    <option value="">Select a course id</option>
                    {courseIds.map(Courses => (
                    <option key={Courses.course_id} value={Courses.course_id}>
                        {Courses.course_id}
                    </option>
                    ))}
                </select>
            </div>
           </form>
        </div>
        </div>
        
    );
  }
  

export default SelectLanguage;

